import{NOTES}from"./notes";export const INTERVALS=["P1","m2","M2","m3","M3","P4","A4/d5","P5","m6","M6","m7","M7","P8/d9","m9/A8","M9/d10","m10/A9","M10/d11","P11/A10","d12/A11","P12/d13","m13/A12","M13/d14","m14/A13","M14/d15","P15/A14","A15"];export function get(n,t){let e=NOTES.indexOf(t)-NOTES.indexOf(n);return e<0&&(e=NOTES.length+e),e}export function convertToDiatonic(n){return n.map(n=>isNaN(n)||null===n?null:INTERVALS[n])}